import axios from "axios";
import MovieCard from "components/MovieCard";
import Pagination from "components/Pagination";
import { useEffect, useState } from "react";

function Listing() {

  const [pageNumber, setPageNumber] = useState(0);
  const [page, setPage] = useState(
    {
      content: [],
      last: true,
      totalPages: 0,
      totalElements: 0,
      size: 12,
      number: 0,
      first: true,
      numberOfElements: 0,
      empty: true
    }
  )
  useEffect(() => {
    axios.get(`localhost:3000/movies?size=12&page=${pageNumber}&sort=id`)
      .then(response => {
        const data = response.data;
        setPage(data);
        // console.log(data)
        // setPageNumber(data.number)
      })
  }, [pageNumber])

  //Forma errada que me retorna 2 vez a requisicao ou seja para previnir esse tipo de comportamento preciso trabalhar com os hooks(use states) do react
  // axios.get(`${BASE_URL}/movies?size=12&page=0`)
  //   .then(response => {
  //     console.log(response.data)
  //   })

  //o Pagination eh quem vai chamar essa funcao
  const handlePageChange = (newPageNumber) => {
    setPageNumber(newPageNumber);
  }

  return (
    <>
      <Pagination page={page} onChange={handlePageChange} />
      <div className="container">
        <div className="row">
          {page.content.map(movie => (
            <div key={movie.id} className="col-sm-6 col-lg-4 col-xl-3 mb-3">
              <MovieCard movie={movie} />
            </div>
          ))}

        </div>
      </div>
    </>
  )
}

export default Listing;
